import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import ESGChart from './ESGChart';

function App() {
  const [company, setCompany] = useState('');
  const [file, setFile] = useState(null);
  const [esgData, setEsgData] = useState(null);
  const [error, setError] = useState('');
  const [uploadedData, setUploadedData] = useState(null);
  const [query, setQuery] = useState(''); // State for natural language query
  const [llmResponse, setLlmResponse] = useState(''); // State for LLM response

  const handleSearch = async () => {
    setError(''); // Clear previous errors
    try {
      const response = await axios.get(`http://localhost:5000/api/company/${company}`);
      setEsgData(response.data[0]); // Assuming response is an array
    } catch (err) {
      setError('Error fetching data');
      setEsgData(null);
    }
  };

  const handleFileUpload = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileSubmit = async () => {
    if (!file) return alert('Please upload a file first!');

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setUploadedData(response.data.data);
    } catch (err) {
      console.error('Error uploading file', err);
      setError('Error uploading file'); // Add error message
    }
  };

  const handleExportCSV = async () => {
    if (!esgData) return alert('No ESG data to export!');
    try {
      const response = await axios.post('http://localhost:5000/api/export/csv', { data: esgData });
      const blob = new Blob([response.data], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'esg-data.csv';
      a.click();
    } catch (err) {
      console.error('Error exporting CSV', err);
      setError('Error exporting CSV'); // Add error message
    }
  };

  const handleExportPDF = async () => {
    if (!esgData) return alert('No ESG data to export!');
    try {
      const response = await axios.post('http://localhost:5000/api/export/pdf', { data: esgData });
      const blob = new Blob([response.data], { type: 'application/pdf' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'esg-report.pdf';
      a.click();
    } catch (err) {
      console.error('Error exporting PDF', err);
      setError('Error exporting PDF'); // Add error message
    }
  };

  // New function to handle querying the LLM
  const handleQuery = async () => {
    setError(''); // Clear previous errors
    try {
      const response = await axios.post('http://localhost:5000/api/query', { query });
      setLlmResponse(response.data.answer);
    } catch (err) {
      console.error('Error querying LLM:', err);
      setError('Error querying LLM');
      setLlmResponse(null);
    }
  };

  return (
    <div className="App">
      <h1>Search ESG Data for a Company</h1>
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter company name"
          value={company}
          onChange={(e) => setCompany(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {error && <p className="error">{error}</p>}

      {esgData && (
        <div className="esg-data-container">
          <h2>{esgData.companyname}</h2>
          <p><strong>Industry:</strong> {esgData.industry}</p>
          <p><strong>Country:</strong> {esgData.country}</p>
          <p><strong>Overall Score:</strong> {esgData["Overall Score"]}</p>
          <p><strong>Transparency Score:</strong> {esgData["Overall Transparency Score"]}</p>
          <p><strong>Environmental Score:</strong> {esgData["Environmental Pillar Score"]}</p>
          <p><strong>Social Score:</strong> {esgData["Social Pillar Score"]}</p>
          <p><strong>Governance Score:</strong> {esgData["Governance Pillar Score"]}</p>

          <button onClick={handleExportCSV}>Export CSV</button>
          <button onClick={handleExportPDF}>Export PDF</button>

          {/* ESG Chart */}
          <ESGChart esgData={esgData} />
        </div>
      )}

      <h2>Upload Historical ESG Data</h2>
      <input type="file" onChange={handleFileUpload} />
      <button onClick={handleFileSubmit}>Upload</button>

      {uploadedData && (
        <div>
          <h2>Uploaded Data</h2>
          <pre>{JSON.stringify(uploadedData, null, 2)}</pre>
        </div>
      )}

      <h2>Ask about ESG Metrics</h2>
      <input
        type="text"
        placeholder="Ask a question about ESG"
        value={query} // Bind to query state
        onChange={(e) => setQuery(e.target.value)} // Update state on change
      />
      <button onClick={handleQuery}>Ask</button>

      {llmResponse && (
        <div>
          <h3>LLM Response</h3>
          <p>{llmResponse}</p>
        </div>
      )}
    </div>
  );
}

export default App;
