// frontend/src/App.js

import React, { useState } from 'react';
import axios from 'axios';  // To make API requests
import './App.css';  // Import styling

function App() {
  const [esgData, setEsgData] = useState(null);
  const [companyName, setCompanyName] = useState('Apple Inc.');  // Default company
  const [error, setError] = useState('');

  // Function to fetch ESG data from backend
  const fetchEsgData = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/esg-data?companyname=${companyName}`);
      setEsgData(response.data.scores);  // Assuming the API returns 'scores' field in data
      setError('');  // Clear any previous errors
    } catch (err) {
      setError('Failed to fetch ESG data');
    }
  };

  return (
    <div className="App">
      <h1>ESG Dashboard</h1>

      {/* Input to select company */}
      <div>
        <input
          type="text"
          value={companyName}
          onChange={(e) => setCompanyName(e.target.value)}
          placeholder="Enter company name"
        />
        <button onClick={fetchEsgData}>Get ESG Data</button>
      </div>

      {/* Display ESG data or error */}
      {error && <p className="error">{error}</p>}
      {esgData ? (
        <div className="esg-data">
          <h2>ESG Scores for {companyName}</h2>
          <p><strong>Environmental:</strong> {esgData.environmental}</p>
          <p><strong>Social:</strong> {esgData.social}</p>
          <p><strong>Governance:</strong> {esgData.governance}</p>
        </div>
      ) : (
        !error && <p>Loading...</p>
      )}
    </div>
  );
}

export default App;
