import React from 'react';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

function ESGChart({ esgData }) {
  const data = {
    labels: ['Overall Score', 'Environmental', 'Social', 'Governance'],
    datasets: [
      {
        label: 'ESG Scores',
        data: [
          esgData['Overall Score'],
          esgData['Environmental Pillar Score'],
          esgData['Social Pillar Score'],
          esgData['Governance Pillar Score'],
        ],
        backgroundColor: ['#4CAF50', '#2196F3', '#FFC107', '#FF5722'],
      },
    ],
  };

  return <Bar data={data} />;
}

export default ESGChart;
