import React from 'react';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';

function ESGChart({ esgData }) {
  if (!esgData) {
    return <p>No ESG data available to display.</p>; // Handle the case when esgData is undefined
  }

  const data = {
    labels: ['Overall Score', 'Environmental', 'Social', 'Governance'],
    datasets: [
      {
        label: 'ESG Scores',
        data: [
          esgData['Overall Score'] || 0,
          esgData['Environmental Pillar Score'] || 0,
          esgData['Social Pillar Score'] || 0,
          esgData['Governance Pillar Score'] || 0,
        ],
        backgroundColor: ['#4CAF50', '#2196F3', '#FFC107', '#FF5722'],
      },
    ],
  };

  return <Bar data={data} />;
}

export default ESGChart;
