import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; // Import your custom CSS

function App() {
  const [company, setCompany] = useState('');
  const [esgData, setEsgData] = useState(null);
  const [error, setError] = useState('');

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/company/${company}`);
      setEsgData(response.data[0]); // Assuming response is an array
      setError('');
    } catch (err) {
      setError('Error fetching data');
      setEsgData(null);
    }
  };

  return (
    <div className="App">
      <h1>Search ESG Data for a Company</h1>
      <div className="search-container">
        <input
          type="text"
          placeholder="Enter company name"
          value={company}
          onChange={(e) => setCompany(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {error && <p className="error">{error}</p>}

      {esgData && (
        <div className="esg-data-container">
          <h2>{esgData.companyname}</h2>
          <p><strong>Industry:</strong> {esgData.industry}</p>
          <p><strong>Country:</strong> {esgData.country}</p>
          <p><strong>Exchange:</strong> {esgData.exchangename}</p>
          <p><strong>Year:</strong> {esgData.Year}</p>
          <p><strong>Overall Score:</strong> {esgData["Overall Score"]}</p>
          <p><strong>Transparency Score:</strong> {esgData["Overall Transparency Score"]}</p>
          <p><strong>Environmental Pillar Score:</strong> {esgData["Environmental Pillar Score"]}</p>
          <p><strong>Social Pillar Score:</strong> {esgData["Social Pillar Score"]}</p>
          <p><strong>Governance Pillar Score:</strong> {esgData["Governance Pillar Score"]}</p>
          <p><strong>Overall Score Global Rank:</strong> {esgData["Overall Score Global Rank"]}</p>
          <p><strong>Overall Industry Rank:</strong> {esgData["Overall Industry Rank"]}</p>
          <p><strong>Region Rank:</strong> {esgData["Overall Region Rank"]}</p>
          <p><strong>Last Updated:</strong> {esgData["Latest Score Date"]}</p>
        </div>
      )}
    </div>
  );
}

export default App;
